INSERT INTO `tbl_product` (`product_id`, `category_id`, `name`, `img1`, `img2`, `gia`, `mota`, `chitiet`, `hot`, `new`, `km`, `mua`, `thich`, `sl_con`) VALUES
(1, '1', 'Áo Sơ Mi Nam Trắng', 'product1_1.jpg', 'product1_2.jpg', 70000, 'Áo sơ mi được làm từ chất liệu vải Lụa Nến mềm mịn, không bai gião, ít bám bụi, ít nhăn\r\n', 'Áo sơ mi được làm từ chất liệu vải Lụa Nến mềm mịn, không bai gião, ít bám bụi, ít nhăn\r\n\r\nÁo sơ mi có phom dáng ôm cơ thể, cổ bẻ ôm vừa phải, nhẹ nhàng tôn dáng người mặc\r\n\r\nĐường may của áo sơ mi rất chắc chắn thoải mái cho người sử dụng có thể vận động\r\n\r\nÁo sơ mi có màu trắng sữa (Trắng dịu) giúp người mặc dễ dàng phối đồ với những bộ vest hay quần âu, quần jean', 1, 0, 20, 100, 81, 60),
(2, '1', 'Áo Sơ Mi Nam KOJIBA Dài Tay Công Sở, Kiểu Dáng Trẻ Trung, Hiện Đại (Nhiều Màu)', 'product2_1.jpg', 'product2_2.jpg', 100000, 'Chất liệu 80% Cotton, 20% Polyester.\r\n\r\nForm áo sơ mi Regular Fit, dáng ôm.\r\n\r\nThiết kế cổ bẻ, áo sơ mi dài tay.\r\n\r\nMade in Vietnam.', '<p>Áo Sơ Mi Nam Dài Tay KOJIBA KTRANG - Trắng giúp bạn nam trông lịch lãm và sang trọng hơn với thiết kế sang trọng, gia công sắc sảo.</p>\r\n<p>Thích hợp mặc trong nhiều môi trường khác nhau như công sở, tiệc, event, du lị</p>\r\n<p>Chất liệu 80% Cotton 20% Polyester.\r\nĐược sử dụng nhiều nhất trên thị trường.\r\nThiết kế chi tiết, tỉ mỉ từng chi tiết dù là nhỏ.</p>\r\n<p>Form áo sơ mi Regular Fit, dáng ôm.\r\nThiết kế cổ bẻ, áo sơ mi dài tay.\r\nMàu sắc: Trắng, Đen, Xanh nhạt, Xanh đậm và Đỏ đô</p>\r\n<p>Size: Từ M đến 3XL</p>\r\n<p>Hương dẫn chọn size</p>\r\nhttps://salt.tikicdn.com/ts/tmp/10/87/fc/4a02bbe535ba7b28b56a98741674b112.png', 0, 1, 10, 87, 57, 150),
(3, '1', 'Áo Sơ Mi Nam Kẻ Caro Cổ Bẻ Dài Tay Phong Cách Hàn Quốc', 'product3_1.jpg', 'product3_2.jpg', 139000, 'Thiết kế cổ bẻ, tay dài, có nút gài phía trước chắc chắn, đường may tỉ mỉ.\r\n\r\nÁo thiết kế form vừa vặn giúp tôn được đường nét mạnh mẽ, nam tính của các chàng trai.\r\n\r\nMàu sắc tinh tế phối với họa tiết nổi bật, mang đến sự thanh lịch, cuốn hút.\r\n\r\nXuất xứ: Việt Nam', '<p>Áo Sơ Mi Nam Caro Dài Tay  - Trắng giúp bạn nam trông lịch lãm và sang trọng hơn với thiết kế sang trọng, gia công sắc sảo.</p>\r\n<p>Thích hợp mặc trong nhiều môi trường khác nhau như công sở, tiệc, event, du lị\r\nChất liệu 80% Cotton 20% Polyester.</p>\r\n<p>Được sử dụng nhiều nhất trên thị trường.\r\nThiết kế chi tiết, tỉ mỉ từng chi tiết dù là nhỏ.</p>\r\n<p>Form áo sơ mi Regular Fit, dáng ôm.</p>\r\n<p>Thiết kế cổ bẻ, áo sơ mi dài tay.</p>\r\nhttps://salt.tikicdn.com/ts/tmp/73/9c/96/c17956e95426ec065431b7191273cc4c.jpg', 1, 1, 0, 140, 57, 87),
(4, '1', 'Áo khoác kaki jean Nam UNISEX - 6 màu căn bản', 'product4_1.jpg', 'product4_2.jpg', 200000, 'Áo khoác UNISEX với chất liệu vải Kaki jean thô bền bỉ mềm mại.\r\n\r\nMàu vải được Wash kỹ hạn chế ra màu khi giặt.\r\n\r\nForm áo vừa vặn.\r\n\r\nĐường may tinh tế, chuẩn từng chi tiết.\r\n\r\nThiết kế nổi bật, phong cách.', '<p>THÔNG TIN SẢN PHẨM:</p>\r\n\r\n<p>+) Form body Hàn Quốc, dễ kết hợp với các loại quần.</p>\r\n\r\n<p>+) Made in Việt Nam.</p>\r\n\r\n<p>+) M, L, XL</p>\r\n\r\n<p>+) Được sử dụng nhiều nhất trên thị trường.</p>\r\n\r\n<p>+) Thích hợp mặc trong nhiều môi trường khác nhau </p>\r\n\r\n<p>--Hàng có sẵn, đủ size: Từ M, L, XL</p>\r\n\r\n<p>Size M:  Nặng khoảng 57kg trở xuống</p>\r\n\r\n<p>Size L:   Nặng khoảng 57kg đến 66kg</p>\r\n\r\n<p>Size XL:   Nặng khoảng 68kg đến 78k</p>', 1, 0, 20, 61, 41, 299),
(5, '1', 'Áo khoác jean nam phong cách Hàn Quốc AKJN012 (Xanh bò)', 'product5_1.jpg', 'product5_2.jpg', 300000, '<p>&Aacute;o kho&aacute;c Jeans với chất liệu vải Demin th&ocirc; d&agrave;y bền bỉ. M&agrave;u vải được nhộm kỹ hạn chế ra m&agrave;u khi giặt. Form &aacute;o body trẻ trung. Đường may tinh tế, chuẩn từng chi tiết. Thiết kế in m&agrave;i bạc, phối hợp mũ ống tay gấu &aacute;o vải nỉnổi bật, phong c&aacute;ch.</p>\r\n', '<ul>\r\n	<li>&Aacute;o Kho&aacute;c Jean Nam phong c&aacute;ch H&agrave;n Quốc Akjn12 với chất liệu jeans th&ocirc; mềm mại gi&uacute;p bạn thoải m&aacute;i vận động, th&iacute;ch hợp mặc kho&aacute;c ngo&agrave;i gi&uacute;p tr&aacute;nh nắng, giữ ấm m&agrave; vẫn hợp thời trang v&agrave; phong c&aacute;ch.</li>\r\n	<li>&Aacute;o c&oacute; thiết kế form rộng trẻ trung dễ d&agrave;ng phối n&oacute; với c&aacute;c loại &aacute;o thun hoặc sơ mi kh&aacute;c.</li>\r\n	<li>N&eacute;t &aacute;o liền lạc v&agrave; theo phong c&aacute;ch hiện đại, tay &aacute;o d&agrave;i phong c&aacute;ch v&agrave; những điểm in h&igrave;nh tạo n&ecirc;n điểm nhấn cho người mặc trước những người xung quanh.</li>\r\n	<li>N&uacute;t &aacute;o d&agrave;y bằng kim loại đảm bảo độ bền chắc khi sử dụng l&acirc;u d&agrave;i. Kh&ocirc;ng chỉ bảo vệ l&agrave;n da khỏi nắng n&oacute;ng, giữ ấm khi lạnh, m&agrave; c&ograve;n l&agrave; điểm nhấn cho gu thời trang ấn tượng.</li>\r\n	<li>C&oacute; thể dễ d&agrave;ng mix c&ugrave;ng nhiều kiểu trang phục y&ecirc;u th&iacute;ch hằng ng&agrave;y v&agrave; tự tin đi đến mọi kh&ocirc;ng gian với phong c&aacute;ch chất lừ của ri&ecirc;ng bạn.</li>\r\n</ul>\r\n\r\n<p>Hướng dẫn chọn size:</p>\r\n\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>Size</td>\r\n			<td>M</td>\r\n			<td>L</td>\r\n			<td>XL</td>\r\n			<td>XXL</td>\r\n		</tr>\r\n		<tr>\r\n			<td>C&acirc;n nặng ph&ugrave; hợp</td>\r\n			<td>&lt; 54kg</td>\r\n			<td>54kg - 63kg</td>\r\n			<td>63kg - 70kg</td>\r\n			<td>70kg - 80kg</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 1, 1, 0, 112, 81, 288),
(6, '2', 'Quần Jogger thể thao Hiphop Nam Nữ Viền Vạch Phản Quang -HẢI ĐĂNG', 'product6_1.jpg', 'product6_2.jpg', 150000, 'Chất Liệu: DA CÁ mềm mịn.\r\n\r\nMàu sắc: đen - trắng.\r\n\r\nThiết kế viền sọc vạch phản quang cực chất.\r\n\r\nMiến phản quang sẽ phát sáng khi có ánh đèn chiếu vào chủ yếu buổi tối ban ngày không phát sáng nha.', '<ul class=\"\">\r\n<li class=\"\" data-spm-anchor-id=\"a2o4n.pdp.product_\">Chất Liệu: Nỉ Bông mềm mịn</li>\r\n<li class=\"\">Màu sắc: đen (black) - trắng (white)</li>\r\n<li class=\"\">Thiết kế viền sọc vạch phản quang cực chất</li>\r\n<li class=\"\">Size tương ứng:</li>\r\n<li class=\"\">M: 38-55kg -</li>\r\n<li class=\"\">L: 56 -65kg -</li>\r\n<li class=\"\">Miến phản quang sẽ phát sáng khi có ánh đèn chiếu vào chủ yếu buổi tối ban ngày không phát sáng nha.</li>\r\n</ul>', 1, 0, 15, 60, 34, 120),
(7, '2', 'Quần jogger Nam Siêu Đẹp, Chất liệu kaki đẹp, kiểu jogger gấu chun, họa tiết kẻ sọc, màu sắc đơn giản khỏe khoắn, form free size, hàng đẹp', 'product7_1.jpg', 'product7_2.jpg', 250000, 'Tên sản phẩm : Quần jogger Nam. \r\n\r\nChất liệu : kaki đẹp. \r\n\r\nHọa tiết : tiết kẻ sọc. \r\n\r\nKiểu/loại : gấu chun. \r\n\r\nMàu sắc : đen, be. \r\n\r\nForm : free size 1m55 -1m65. \r\n\r\nXuất xứ thương hiệu : Việt Nam. \r\n\r\nNơi sản xuất : Việt Nam. ', '<ol>\r\n<li>Quần jogger nam cực chất, phong cách hàn đẹp, cool ngầu, cá tính.</li>\r\n<li>Form quần free size, màu sắc đơn giản đen và be, gồm có 4 túi, ống bó, gấu chun.</li>\r\n<li>Được thiết kế và sản xuất theo quy trình chất lượng nên người dùng có thể yên tâm lựa chọn.</li>\r\n<li>Phối hợp với nhiều loại áo tùy theo phong cách người dùng muốn phối ( áo bò, áo phông, áo thể thao ).</li>\r\n<li>Diện đi chơi, đi dạo,du lịch, đi học </li>\r\n</ol>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p><img id=\"https://salt.tikicdn.com/ts/tmp/31/b5/10/75cf7ff54f9e779538730a268003be05.jpg\" title=\"\" src=\"https://salt.tikicdn.com/ts/tmp/31/b5/10/75cf7ff54f9e779538730a268003be05.jpg\" alt=\"\" width=\"750\" height=\"750\" /></p>\r\n<p> </p>\r\n<p><img id=\"https://salt.tikicdn.com/ts/tmp/d1/63/f8/9cccca56eb07cd332c01855de0960022.jpg\" title=\"\" src=\"https://salt.tikicdn.com/ts/tmp/d1/63/f8/9cccca56eb07cd332c01855de0960022.jpg\" alt=\"\" width=\"750\" height=\"1074\" /></p>\r\n<p><img id=\"https://salt.tikicdn.com/ts/tmp/b9/6e/63/6df82cb56249dab39e863c2d6c67ed36.jpg\" title=\"\" src=\"https://salt.tikicdn.com/ts/tmp/b9/6e/63/6df82cb56249dab39e863c2d6c67ed36.jpg\" alt=\"\" width=\"750\" height=\"956\" /></p>\r\n<p><img id=\"https://salt.tikicdn.com/ts/tmp/b1/56/e0/886886b7eb895e53eb293c5c6322d1e8.jpg\" title=\"\" src=\"https://salt.tikicdn.com/ts/tmp/b1/56/e0/886886b7eb895e53eb293c5c6322d1e8.jpg\" alt=\"\" width=\"750\" height=\"750\" /></p>\r\n<p><img id=\"https://salt.tikicdn.com/ts/tmp/de/3d/4f/e9bd9a0483d48caba02263ce5b26