--
-- Cấu trúc cho view `rating`
--
DROP TABLE IF EXISTS `rating`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `rating`  AS  select `tbl_product`.`product_id` AS `product_id`,`tbl_product`.`img1` AS `img`,`tbl_product`.`name` AS `name`,count(`tbl_cmt`.`cmt_id`) AS `socmt`,avg(`tbl_cmt`.`rating`) AS `rating` from (`tbl_cmt` join `tbl_product`) where `tbl_product`.`product_id` = `tbl_cmt`.`product_id` group by `tbl_cmt`.`product_id` ;

